
public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		        boolean x = true, y = false;

		        // Logical AND
		        System.out.println("x && y: " + (x && y));

		        // Logical OR
		        System.out.println("x || y: " + (x || y));

		        // Logical NOT
		        System.out.println("!x: " + !x);
		        System.out.println("!y: " + !y);
		    }
		

	

}
